package basic.dev;

public class MainApp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a = 100, b = 10;
 
        System.out.println(a + b);
        System.out.println(a - b);
        System.out.println(a  * b);
        System.out.println(a / b);
                
        int c = 50;
        System.out.println(c > a);
        System.out.println(c > b);
        System.out.println(c < a);
        System.out.println(c < b);
        System.out.println(c <= a);
        System.out.println(c <= b);
        System.out.println(c >= a);
        System.out.println(c >= b);
        
        
	}
       
        
        	
}
